import React from 'react';
import './App.css';
import Course from './Course';

class CourseArea extends React.Component {
  getCourses() {
    let courses = [];
    if(this.props.id === "Completed Course"){
      let completedcourses=this.props.completedCourses;
      let coursedata= this.props.courseData;
      console.log(completedcourses);

      for( let i =0; i < completedcourses.length; i++){
        console.log( coursedata[0].number);
        console.log( completedcourses[0]);
        console.log( coursedata[0].nummber === completedcourses[0]);
        // for( let j=0; j<coursedata.length; j++){
        //   console.log( completedcourses[i]);
        //   console.log( coursedata[0].nummber === completedcourses[0]);
        //   if(coursedata[j].nummber === completedcourses[i]){
        //     console.log( coursedata[j]);
        //     courses.push(coursedata[j]);
        //     break;
        //   }
        // }
      }
      console.log( courses);
      return courses;
    }

    if (Array.isArray(this.props.data)){
      for(let i =0; i < this.props.data.length; i++){
        courses.push (
          <Course key={i} data={this.props.data[i]} courseKey={this.props.data[i].number} addCartCourse={(data) => this.props.addCartCourse(data)} removeCartCourse={(data) => this.props.removeCartCourse(data)} cartCourses={this.props.cartCourses}/>
        )
      }
    }
    else{
      for(const course of Object.keys(this.props.data)){
        courses.push (
          <Course key={this.props.data[course].number} data={this.props.data[course]} courseKey={this.props.data[course].number} addCartCourse={(data) => this.props.addCartCourse(data)} removeCartCourse={(data) => this.props.removeCartCourse(data)} cartCourses={this.props.cartCourses}/>
        )
      }
    }

    return courses;
  }

  shouldComponentUpdate(nextProps) {
    return (JSON.stringify(this.props) !== JSON.stringify(nextProps))
  }

  render() {
    return (
      <div style={{margin: 5, marginTop: -5}}>
        {this.getCourses()}
      </div>
    )
  }
}

export default CourseArea;
